import React, { Component } from 'react'
import { Link } from 'react-router-dom';
import '../SignStyle.css';


export class SignUp extends Component {




    constructor(props) {
        super(props);
        this.state = {
            name: "",
            email: "",
            password: "",
            verify_password: ""
        };
    }

    onChangeName = (name) => {
        this.setState({ name: name.target.value.trim() })
    }
    onChangeEmail = (Email) => {
        this.setState({ email: Email.target.value.trim() })
    }
    onChangePassword = (Password) => {
        this.setState({ password: Password.target.value.trim() })
    }
    onChangeVerPassword = (VerPassword) => {
        this.setState({ verify_password: VerPassword.target.value.trim() })
    }



    checkButton = () => {

        const nameLabel = document.getElementById('nameLabel');
        const emailLabel = document.getElementById('emailLabel');
        const passwordLabel = document.getElementById('passwordLabel');
        const ver_passwordLabel = document.getElementById('ver_passwordLabel');


        if (this.state.name === 0) { nameLabel.innerText = 'Enter a valid user name' }
        if (this.state.name < 8) { nameLabel.innerText = 'User name must be greater than 8 letters' }
        if (this.state.name > 20) { nameLabel.innerText = 'User name must be less than 20 letters' }
        //
        if (this.state.name !== 0 && this.state.name > 8  && this.state.name < 20 ) { nameLabel.innerText = "" }
        //

        else if (this.state.email === 0) { emailLabel.innerText = 'Enter a valid email adress' }
        else if (this.state.email < 8) { emailLabel.innerText = 'Eamil must be greater than 8 letters' }
        else if (this.state.email > 20) { emailLabel.innerText = 'Email name must be less than 20 letters' }
        else if (!this.state.email.includes("@" && ".")) { emailLabel.innerText = 'Email must contain @' }

        //
        if (this.state.email !== 0 && this.state.email > 8 && this.state.email < 20 && this.state.email.includes("@" && ".")) { emailLabel.innerText = '' }
        //

        if (this.state.password === 0) { passwordLabel.innerText = 'Enter a valid password' }
        else if (this.state.password < 8) { passwordLabel.innerText = 'password must be greater than 8 letters' }
        else if (this.state.password > 20) { passwordLabel.innerText = 'password name must be less than 20 letters' }
        else if (!this.state.password.match(/[A-Z]/) && !this.state.password.match(/[a-z]/ && !this.state.password.match(/[@/$/#/%/?/^/*/!/~/-/=/+/_/-]/))) { passwordLabel.innerText = 'password must contain : at least one lowercase alphabet , at least one uppercase alphabet , at least one special chracter (@$#%?^*!~-=+_-) '}

        //
        if (this.state.password !== 0 && this.state.password > 8 && this.state.password < 20 && this.state.password.match(/[A-Z]/) && this.state.password.match(/[a-z]/ && this.state.password.match(/[@/$/#/%/?/^/*/!/~/-/=/+/_/-]/))) { passwordLabel.innerText = "" }
        //

        else if (this.state.password !== this.state.verify_password) { ver_passwordLabel.innerText = 'Check your password verfication' }
        else if (this.state.verify_password === 0) { ver_passwordLabel.innerText = "This can't be empty" }

        else if (this.state.password === this.state.verify_password && this.state.verify_password !== 0) { ver_passwordLabel.innerText = "" }

        else (
            document.getElementById('checkButton').disabled = false
        )




    }

    render() {
        return (
            <div>
                <div className="wrapper fadeInDown mt-5 pt-5">
                    <div id="formContent">
                        <h2 className="active signh2"> Sign Up </h2>
                        <div className="fadeIn first mt-2 mb-2">
                            <i className="fa-solid fa-user fa-6x"></i>
                        </div>


                        <form onChange={this.checkButton} action="/Dashboard" method="POST" >

                            <input
                                type="text"
                                name="name"
                                placeholder="User name"
                                onChange={this.onChangeName}
                                onBlur={this.checkButton}
                                autoComplete="off"
                            />

                            <label id="nameLabel" style={{ color: "red", fontSize: "13px" }}></label>


                            <input
                                type="email"
                                name="email"
                                placeholder="Email adress"
                                onChange={this.onChangeEmail}
                                onBlur={this.checkButton}
                                autoComplete="off"
                            />

                            <label id="emailLabel" style={{ color: "red", fontSize: "13px" }}></label>



                            <input
                                type="password"
                                name="password"
                                placeholder="Password"
                                onChange={this.onChangePassword}
                                onBlur={this.checkButton}
                                autoComplete="off"
                            />

                            <label id="passwordLabel" style={{ color: "red", fontSize: "13px" }}></label>

                            <input
                                type="password"
                                name="verify_password"
                                placeholder="Verify Password"
                                onChange={this.onChangeVerPassword}
                                onBlur={this.checkButton}
                                autoComplete="off"
                            />

                            <label id="ver_passwordLabel" style={{ color: "red", fontSize: "13px" }}></label>

                            <br />
                            <input type="submit" className="fadeIn Submitinput" id="checkButton" value="SIGN UP" disabled />

                        </form>

                        <div id="formFooter">
                            <Link className="underlineHover" to="/login">Already a member ?</Link>
                        </div>


                    </div>
                </div></div>
        )
    }
}

export default SignUp
