import React, { Component } from 'react'
import { Link } from 'react-router-dom';
import '../SignStyle.css';
import ReactFormInputValidation from "react-form-input-validation";


export class SignUp extends Component {




    constructor(props) {
        super(props);
        this.state = {
            fields: {
                email: "",
                verify_email: "",
                password: "",
                verify_password: ""
            },
            errors: {}
        };
        this.form = new ReactFormInputValidation(this);
        this.form.useRules({
            email: "required|email|min:6",
            verify_email: "required|email",
            password: "required|digits_between:8,20|numeric",
            verify_password: "required|digits_between:8,20|numeric"
        });
    }

    render() {
        return (
            <div>
                <div className="wrapper fadeInDown mt-5 pt-5">
                    <div id="formContent">
                        <h2 className="active signh2"> Sign Up </h2>

                        <div className="fadeIn first mt-2 mb-2">
                            <i className="fa-solid fa-user fa-6x"></i>
                        </div>

                        {/* <form onSubmit={this.handleSubmit} action="/Dashboard">
                            <input type="email" id="email" className="fadeIn second" name="login" value={this.state.user_email} placeholder="Email adress" required minLength="8" maxLength="28" />
                            <input type="email" id="Veremail" className="fadeIn second" name="login" placeholder="Verify email" />
                            <input type="password" id="password" className="fadeIn third" name="login" placeholder="password" />
                            <input type="password" id="Verpassword" className="fadeIn third" name="login" placeholder="re-enter password" />
                            <input type="submit" className="fadeIn fourth" value="Sign up" />
                        </form> */}


                        <form onSubmit={this.handleSubmit} action="/Dashboard" method="GET" >
                            <input
                                type="email"
                                name="email"
                                onBlur={this.form.handleBlurEvent}
                                onChange={this.form.handleChangeEvent}
                                value={this.state.fields.email}
                                placeholder="Email adress"
                            />
                            {this.state.errors.email && (
                                <div>
                                    <label className="error">{this.state.errors.email ? this.state.errors.email : ""}</label>
                                </div>
                            )}

                            <input
                                type="email"
                                name="verify_email"
                                onBlur={this.form.handleBlurEvent}
                                onChange={this.form.handleChangeEvent}
                                value={this.state.fields.ver_email}
                                placeholder="Email adress"
                            />
                            {this.state.errors.email && (
                                <div>
                                    <label className="error">{this.state.errors.ver_email ? this.state.errors.ver_email : ""}</label>
                                </div>
                            )}

                            <input
                                type="password"
                                name="password"
                                onBlur={this.form.handleBlurEvent}
                                onChange={this.form.handleChangeEvent}
                                value={this.state.fields.password}
                                placeholder="Password"
                            />
                            {this.state.errors.password && (
                                <div>
                                    <label className="error">{this.state.errors.password ? this.state.errors.password : ""}</label>
                                </div>
                            )}

                            <input
                                type="password"
                                name="verify_password"
                                onBlur={this.form.handleBlurEvent}
                                onChange={this.form.handleChangeEvent}
                                value={this.state.fields.verify_password}
                                placeholder="Verify Password"
                            />
                            {this.state.errors.verify_password && (
                                <div>
                                    <label className="error">{this.state.errors.verify_password ? this.state.errors.verify_password : ""}</label>
                                </div>
                            )}

                            <input type="submit" className="fadeIn fourth" value="Sign Up" />
                        </form>

                        <div id="formFooter">
                            <Link className="underlineHover" to="/login">Already a member ?</Link>
                        </div>

                    </div>
                </div></div>
        )
    }
}

export default SignUp
