import { red } from '@mui/material/colors';
import React, { Component } from 'react'
import { Link } from 'react-router-dom';
import '../SignStyle.css';


export class SignUp extends Component {




    constructor(props) {
        super(props);
        this.state = {
            email: "",
            verify_email: "",
            password: "",
            verify_password: ""
        };
    }









    onChangeEmail = (Email) => {
        this.checkButton()
        this.setState({ email: Email.target.value })
    }
    onChangeVerEmail = (VerEmail) => {
        this.checkButton()
        this.setState({ verify_email: VerEmail.target.value })
    }
    onChangePassword = (Password) => {
        this.checkButton()
        this.setState({ password: Password.target.value })
    }
    onChangeVerPassword = (VerPassword) => {
        this.checkButton()
        this.setState({ verify_password: VerPassword.target.value })
    }

    checkButton = () => {

        if (this.state.email.length === 0) { document.getElementById("emailLabel").innerHTML = ' your email is empty' }

        else if (!this.state.email.con("@student.stc.edu")) { alert('Must end with in: @student.stc.edu"') }

        else if (this.state.email.length < 6) { alert('your email is very short '); }

        else if (this.state.email !== this.state.verify_email) { alert('your email verfication is wrong') }



        else if (this.state.password.length === 0) { alert('A password: ' + this.state.password + 'is empty'); }

        else if (this.state.password.length < 6) { alert('A password : ' + this.state.password + 'is small'); }

        else if (this.state.password !== this.state.verify_password) { alert('Confirmpassword '); }


        else {
            document.getElementById("checkButton").disabled = false;
        }
    }

    render() {
        return (
            <div>
                <div className="wrapper fadeInDown mt-5 pt-5">
                    <div id="formContent">
                        <h2 className="active signh2"> Sign Up </h2>
                        <div className="fadeIn first mt-2 mb-2">
                            <i className="fa-solid fa-user fa-6x"></i>
                        </div>


                        <form id="signUpForm" action="/Dashboard">
                            <input
                                type="email"
                                name="email"
                                placeholder="Email adress"
                                onChange={this.onChangeEmail}
                            />

                            <label id="emailLabel" style={{ color: "red", fontSize: "13px" }}></label>


                            <input
                                type="email"
                                name="verify_email"
                                placeholder="Email adress"
                                onChange={this.onChangeVerEmail}
                            />

                            <label id="ver_emailLabel" style={{ color: "red", fontSize: "13px" }}></label>


                            <input
                                type="password"
                                name="password"
                                placeholder="Password"
                                onChange={this.onChangePassword}
                            />

                            <label id="passwordLabel" style={{ color: "red", fontSize: "13px" }}></label>

                            <input
                                type="password"
                                name="verify_password"
                                placeholder="Verify Password"
                                onChange={this.onChangeVerPassword}
                            />

                            <label id="ver_passwordLabel" style={{ color: "red", fontSize: "13px" }}></label>


                            <button type="submit" className="btn btn-primary" id="checkButton" disabled>SIGN UP</button>


                        </form>

                        <div id="formFooter">
                            <Link className="underlineHover" to="/login">Already a member ?</Link>
                        </div>

                    </div>
                </div></div>
        )
    }
}

export default SignUp
