import React, { Component } from 'react'
import { Link } from 'react-router-dom'
i


export class SignIn extends Component {


    constructor(props) {
        super(props);
        this.state = {
            fields: {
                firstname: "",
                lastname: "",
                subject: ""
            },
            errors: {}
        };
        this.form = new ReactFormInputValidation(this);
        this.form.useRules({
            firstname: "required",
            subject: "required"
        });

        this.formFields = this.formFields.bind(this);

        // Subscribe onformsubmit events
        this.form.addEventListener("formsubmit", this.formFields);
        // or
        this.form.onformsubmit = fields => {
            alert(JSON.stringify(fields));
        };

        // Unsubscribe onformsubmit events.
        this.form.removeEventListener("formsubmit", this.formFields);
    }

    formFields(fields) {
        alert(JSON.stringify(fields));
    }

    render() {
        return (
            <React.Fragment>
                <h3>React Form Input Validation</h3>

                <div className="container">
                    <form onSubmit={this.form.handleSubmit}>
                        <label htmlFor="fname">First Name</label>
                        <input
                            type="text"
                            id="fname"
                            name="firstname"
                            placeholder="Your name.."
                            value={this.state.fields.firstname}
                            onChange={this.form.handleChangeEvent}
                            onBlur={this.form.handleBlurEvent}
                        />
                        {this.state.errors.firstname && (
                            <div>
                                <label className="error">{this.state.errors.firstname}</label>
                            </div>
                        )}

                        <label htmlFor="lname">Last Name</label>
                        <input
                            type="text"
                            id="lname"
                            name="lastname"
                            placeholder="Your last name.."
                            value={this.state.fields.lastname}
                            onChange={this.form.handleChangeEvent}
                            onBlur={this.form.handleBlurEvent}
                        />

                        <label htmlFor="subject">Subject</label>
                        <textarea
                            id="subject"
                            name="subject"
                            placeholder="Write something.."
                            value={this.state.fields.subject}
                            onChange={this.form.handleChangeEvent}
                            onBlur={this.form.handleBlurEvent}
                        />
                        {this.state.errors.subject && (
                            <div>
                                <label className="error">{this.state.errors.subject}</label>
                            </div>
                        )}
                        <input type="submit" value="Submit" />
                    </form>
                </div>
            </React.Fragment>
        );
    }
}

export default SignIn
