import React, { Component } from 'react'
import { getById } from './Audi/carsData/carsDataRes';

import Box from '@mui/material/Box';
import Rating from '@mui/material/Rating';
import Typography from '@mui/material/Typography';

import { useState } from 'react';



import Aos from 'aos';
import 'aos/dist/aos.css'
import { Link } from 'react-router-dom';

import {
    MDBValidation,
    MDBInput,
    MDBBtn,
    MDBCheckbox
} from 'mdb-react-ui-kit';

export class carS extends Component {

    state = {
        shippingPrice: 1354,
        loading: true,
        product: {}
    }

    componentDidMount() {
        var RandomNumber = Math.floor(Math.random() * 500) + 200;
        const id = this.props.match.params.id;
        getById(id)
            .then(product => {
                setTimeout(() => {
                    this.setState({ product, loading: false });
                }, RandomNumber);
            })

        Aos.init();
    }





    render() {


        const RandomFeesNum = Math.floor(Math.random() * 2) + .9;


        if (this.state.loading)
            return <div class="mt-5 pt-5 pb-5 mb-5" ><div class="loader text-center m-auto"></div></div>;
        return (
            <div>
                <div className="container mb-5 mt-5">
                    <div className="row">
                        <div className="col-lg-7 mt-5 mb-5 text-center">
                            <h2>{this.state.product.name}</h2>
                            <h4>${parseInt(this.state.product.price)}</h4>
                            <img src={this.state.product.img} alt={this.state.product.name} width="250px" style={{borderRadius : "3px"}} />
                        </div>
                        <div className="col-lg-5 mt-5 mb-5 card p-3">
                            <p>Shipping Price : ${this.state.shippingPrice}</p>
                            <h5>Car Price : $ {this.state.product.price}</h5>
                            <p>Fees : $ {RandomFeesNum}</p>
                            <hr />
                            <h4 >Total Price : <span style={{color : "green"}}>${parseInt(this.state.product.price) + this.state.shippingPrice + RandomFeesNum}</span></h4>
                        </div>
                    </div>
                    <div className="mt-5 pt-5">
                        <ValidateForm />
                    </div>

                </div>


            </div>
        )
    }
}



function ValidateForm() {

    const [formValue, setFormValue] = useState({
        fname: 'Mark',
        lname: 'Otto',
        email: '',
        city: '',
        state: '',
        zip: '',
    });

    const onChange = (e) => {
        setFormValue({ ...formValue, [e.target.name]: e.target.value });
    };

    return (
        <MDBValidation className='row g-3' validation="/Cart_Buy_Success">
            <div className='col-md-4'>
                <MDBInput name='fname' id='validationCustom01' required label='First name' validation='Valid First Name!'
                />
            </div>
            <div className='col-md-4'>
                <MDBInput name='lname' id='validationCustom01' required label='Last name' validation='Valid Last Name!'
                />
            </div>
            <div className='col-md-4'>
                <div className='input-group has-validation'> <span className='input-group-text' id='phoneNumber'><i class="fa fa-phone" aria-hidden="true"></i></span><input type='text' className='form-control' id='validationCustomUsername' placeholder='Phone number' required />
                    <div className='invalid-feedback'>Please choose a username.</div>
                </div>
            </div>
            <div className='col-md-6'>
                <MDBInput name='city' id='validationCustom03' required label='City' validation='Please provide a valid city.' invalid
                />
            </div>
            <div className='col-md-6'>
                <MDBInput name='zipCode' id='validationCustom05' required label='Zip code' validation='Please provide a valid zip.' invalid
                />
            </div>
            <div className='col-12'>
                <MDBCheckbox label='Agree to terms and conditions' id='invalidCheck' validation='You must agree before submitting.' invalid required
                />
            </div>
            <div className='col-8 mt-5'>
                <button className="btn btn-success" type="submit">Pay Now</button>
            </div>
        </MDBValidation>
    );
}



export default carS
