import React, { Component } from 'react'
import { Link } from 'react-router-dom'
import ReactFormInputValidation from "react-form-input-validation";


export class SignIn extends Component {


    constructor(props) {
        super(props);
        this.state = {
            fields: {
                user_email: "",
                user_pass: "",
            },
            errors: {}
        };
        this.form = new ReactFormInputValidation(this);
        this.form.useRules({
            user_email: "required",
            user_pass: "required"
        });

        this.formFields = this.formFields.bind(this);

        // Subscribe onformsubmit events
        this.form.addEventListener("formsubmit", this.formFields);
        // or
        this.form.onformsubmit = fields => {
            alert(JSON.stringify(fields));
        };

        // Unsubscribe onformsubmit events.
        this.form.removeEventListener("formsubmit", this.formFields);
    }

    formFields(fields) {
        alert(JSON.stringify(fields));
    }

    render() {
        return (
            <React.Fragment>
                <div className="wrapper fadeInDown">
                    <div id="formContent">
                        <h2 className="active signh2" > Sign In </h2>

                        <div className="fadeIn first">
                            <i className="fa-solid fa-user fa-6x mb-2 mt-2"></i>
                        </div>

                        <form onSubmit={this.form.handleSubmit}>
                            <input
                                type="email"
                                id="email"
                                name="user_email"
                                placeholder="Email adress"
                                className="fadeIn second"
                                value={this.state.fields.user_email}
                                onChange={this.form.handleChangeEvent}
                                onBlur={this.form.handleBlurEvent}
                            />
                            {this.state.errors.user_email && (
                                <div>
                                    <label className="error">{this.state.errors.user_email}</label>
                                </div>
                            )}
                            <input
                                type="password"
                                id="pass"
                                name="user_pass"
                                placeholder="Password"
                                className="fadeIn third"
                                value={this.state.fields.user_pass}
                                onChange={this.form.handleChangeEvent}
                                onBlur={this.form.handleBlurEvent}
                            />
                            <input type="submit" className="fadeIn fourth" value="Log In" />
                        </form>

                        <div id="formFooter">
                            <Link className="underlineHover" to="/forget-password">Forgot Password?</Link>
                            <br />
                            <Link className="underlineHover" to="/rigister">Create an account ?</Link>
                        </div>
                    </div>
                </div>

            </React.Fragment>
        );
    }
}

export default SignIn





