import React, { Component } from 'react'
import { Link } from 'react-router-dom';
import '../SignStyle.css';


export class SignUp extends Component {




    constructor(props) {
        super(props);
        this.state = {
            email: "",
            verify_email: "",
            password: "",
            verify_password: ""
        };
    }









    onChangeEmail = (Email) => {
        this.setState({ email: Email.target.value })
    }
    onChangeVerEmail = (VerEmail) => {
        this.setState({ verify_email: VerEmail.target.value })
    }
    onChangePassword = (Password) => {
        this.setState({ password: Password.target.value })
    }
    onChangeVerPassword = (VerPassword) => {
        this.setState({ verify_password: VerPassword.target.value })
    }

    checkButton = () => {

        const errorEmail = document.getElementById("emailLabel");
        const errorVerEmail = document.getElementById("ver_emailLabel");
        const errorPassword = document.getElementById("passwordLabel");
        const errorVerPassword = document.getElementById("ver_passwordLabel");


        if (this.state.email.length === 0) {  errorEmail.innerText = ' your email is empty' }

        else if (!this.state.email.includes("@" , ".")) { errorEmail.innerText = "Email must contain's : @" }

        else if (this.state.email.length < 6) { errorEmail.innerText = 'Email is very short ' }

        else if (this.state.email !== this.state.verify_email) { errorVerEmail.innerText = 'Verify your email' }


        else if (this.state.password.length === 0) { errorPassword.innerText = 'Please enter a valid password ' }

        else if (this.state.password.length < 6) { errorPassword.innerText = 'Your password too short' }

        else if (this.state.password !== this.state.verify_password) { errorVerPassword.innerText ='Verify your password confirmation' }


        else {
            document.getElementById("checkButton").disabled = false;
        }


        
    }

    render() {
        return (
            <div>
                <div className="wrapper fadeInDown mt-5 pt-5">
                    <div id="formContent">
                        <h2 className="active signh2"> Sign Up </h2>
                        <div className="fadeIn first mt-2 mb-2">
                            <i className="fa-solid fa-user fa-6x"></i>
                        </div>


                        <form id="signUpForm" action="/Dashboard" autoComplete >
                            <input
                                type="email"
                                name="email"
                                placeholder="Email adress"
                                onChange={this.onChangeEmail}
                                onBlur={this.checkButton}
                                autocomplete="off"
                            />

                            <label id="emailLabel" style={{ color: "red", fontSize: "13px" }}></label>


                            <input
                                type="email"
                                name="verify_email"
                                placeholder="Email adress"
                                onChange={this.onChangeVerEmail}
                                onBlur={this.checkButton}
                                autocomplete="off"
                            />

                            <label id="ver_emailLabel" style={{ color: "red", fontSize: "13px" }}></label>


                            <input
                                type="password"
                                name="password"
                                placeholder="Password"
                                onChange={this.onChangePassword}
                                onBlur={this.checkButton}
                                autocomplete="off"
                            />

                            <label id="passwordLabel" style={{ color: "red", fontSize: "13px" }}></label>

                            <input
                                type="password"
                                name="verify_password"
                                placeholder="Verify Password"
                                onChange={this.onChangeVerPassword}
                                onBlur={this.checkButton}
                                autocomplete="off"
                            />

                            <label id="ver_passwordLabel" style={{ color: "red", fontSize: "13px" }}></label>

                            <br/>
                            <input type="submit" className="fadeIn fourth" id="checkButton" disabled>SIGN UP</input>


                        </form>

                        <div id="formFooter">
                            <Link className="underlineHover" to="/login">Already a member ?</Link>
                        </div>

                    </div>
                </div></div>
        )
    }
}

export default SignUp
