import React, { Component } from 'react'
import { Link } from 'react-router-dom'
import ReactFormInputValidation from "react-form-input-validation";


export class SignIn extends Component {


    constructor(props) {
        super(props);
        this.state = {
            fields: {
                name: "",
                email: "",
                phone_number: ""
            },
            errors: {}
        };
        this.form = new ReactFormInputValidation(this);
        this.form.useRules({
            name: "required",
            email: "required|email",
            phone_number: "required|numeric|digits_between:10,12",
        });
        this.form.onformsubmit = (fields) => {
            // Do you ajax calls here.
        }
    }

    render() {
        return (
            <React.Fragment>
                <div className="wrapper fadeInDown">
                    <div id="formContent">
                        <h2 className="active signh2" > Sign In </h2>
                        <div className="fadeIn first">
                            <i className="fa-solid fa-user fa-6x mb-2 mt-2"></i>
                        </div>

                        <form onSubmit={this.form.handleSubmit} action="/Dashboard" method="GET" >
                            <input
                                type="email"
                                name="name"
                                onBlur={this.form.handleBlurEvent}
                                onChange={this.form.handleChangeEvent}
                                value={this.state.fields.name}
                            />
                            {this.state.errors.user_email && (
                                <div>
                                    <label className="error">{this.state.errors.email ? this.state.errors.email : ""}</label>
                                </div>
                            )}
                            <input
                                type="password"
                                name="password"
                                onBlur={this.form.handleBlurEvent}
                                onChange={this.form.handleChangeEvent}
                                value={this.state.fields.password}
                            />
                            {this.state.errors.password && (
                                <div>
                                    <label className="error">{this.state.errors.password ? this.state.errors.password : ""}</label>
                                </div>
                            )}

                            <input type="submit" className="fadeIn fourth" value="Log In" />
                        </form>

                        <div id="formFooter">
                            <Link className="underlineHover" to="/forget-password">Forgot Password?</Link>
                            <br />
                            <Link className="underlineHover" to="/register">Create an account ?</Link>
                        </div>
                    </div>
                </div>

            </React.Fragment>
        );
    }
}

export default SignIn
