import React, { Component } from 'react'
import { Link } from 'react-router-dom';
import '../SignStyle.css';


export class SignUp extends Component {




    constructor(props) {
        super(props);
        this.state = {
            name: "",
            email: "",
            password: "",
            verify_password: ""
        };
    }

    onChangeName = (name) => {
        this.setState({ name: name.target.value.trim() })
    }
    onChangeEmail = (Email) => {
        this.setState({ email: Email.target.value.trim() })
    }
    onChangePassword = (Password) => {
        this.setState({ password: Password.target.value.trim() })
    }
    onChangeVerPassword = (VerPassword) => {
        this.setState({ verify_password: VerPassword.target.value.trim() })
    }



    checkButton = () => {

        const nameLabel = document.getElementById('nameLabel');
        const emailLabel = document.getElementById('emailLabel');
        const passwordLabel= document.getElementById('passwordLabel');
        const ver_passwordLabel = document.getElementById('ver_passwordLabel');


        if (this.state.name === 0) {nameLabel.innerText = 'Enter a valid user name'}
        else if (this.state.name  < 8) {nameLabel.innerText = 'User name must be greater than 8 letters'}
        else if (this.state.name > 20 ) {nameLabel.innerText = 'User name must be less than 20 letters'}

        else if (this.state.email === 0) {emailLabel.innerText = 'Enter a valid email adress'}
        else if (this.state.email  < 8) {emailLabel.innerText = 'Eamil must be greater than 8 letters'}
        else if (this.state.email > 20 ) {emailLabel.innerText = 'Email name must be less than 20 letters'}


    }

    render() {
        return (
            <div>
                <div className="wrapper fadeInDown mt-5 pt-5">
                    <div id="formContent">
                        <h2 className="active signh2"> Sign Up </h2>
                        <div className="fadeIn first mt-2 mb-2">
                            <i className="fa-solid fa-user fa-6x"></i>
                        </div>


                        <form id="signUpForm" onSubmit={() => { console.log(this.state); }} action="/Dashboard" method="POST" >
                            
                            <input
                                type="text"
                                name="name"
                                placeholder="User name"
                                onChange={this.onChangeName , this.checkButton}
                                autocomplete="off"
                            />

                            <label id="nameLabel" style={{ color: "red", fontSize: "13px" }}></label>


                            <input
                                type="email"
                                name="email"
                                placeholder="Email adress"
                                onChange={this.onChangeEmail}
                                autocomplete="off"
                            />

                            <label id="emailLabel" style={{ color: "red", fontSize: "13px" }}></label>



                            <input
                                type="password"
                                name="password"
                                placeholder="Password"
                                onChange={this.onChangePassword}
                                autocomplete="off"
                            />

                            <label id="passwordLabel" style={{ color: "red", fontSize: "13px" }}></label>

                            <input
                                type="password"
                                name="verify_password"
                                placeholder="Verify Password"
                                onChange={this.onChangeVerPassword}
                                autocomplete="off"
                            />

                            <label id="ver_passwordLabel" style={{ color: "red", fontSize: "13px" }}></label>

                            <br />
                            <input type="submit" className="fadeIn Submitinput" id="checkButton" value="SIGN UP" disabled />

                        </form>

                        <div id="formFooter">
                            <Link className="underlineHover" to="/login">Already a member ?</Link>
                        </div>

                    </div>
                </div></div>
        )
    }
}

export default SignUp
