import React, { Component } from 'react'
import { Fragment } from 'react'
import { Link } from 'react-router-dom';
import Buy from '../../../Buy/Buy';
import dataCar from "../carsData/carsData.json";

import Box from '@mui/material/Box';
import Rating from '@mui/material/Rating';
import Typography from '@mui/material/Typography';



import Aos from 'aos';
import 'aos/dist/aos.css'


export class CarSignleAudi1 extends Component {


    constructor(props) {
        super(props);
        this.state = {
            name: "",
            price: "",
            img: "",
            Reviews: "",
            Model: "",
            Exterior_color: "",
            Interior_color: "",
            Drivetrain: "",
            MPG: "",
            Fuel_type: "",
            Transmission: "",
            Engine: "",
            VIN: "",
            Stock: "",
            Mileage: "",
            Vehicle_history: "",
            Link: "",
            rated : ""
        }
    }


    setStateToNewData = () => {
        this.setState({
            name: dataCar[0].name,
            price: dataCar[0].price,
            img: dataCar[0].img,
            Reviews: dataCar[0].Reviews,
            Model: dataCar[0].Model,
            Exterior_color: dataCar[0].Exterior_color,
            Interior_color: dataCar[0].Interior_color,
            Drivetrain: dataCar[0].Drivetrain,
            MPG: dataCar[0].MPG,
            Fuel_type: dataCar[0].Fuel_type,
            Transmission: dataCar[0].Transmission,
            Engine: dataCar[0].Engine,
            VIN: dataCar[0].VIN,
            Stock: dataCar[0].Stock,
            Mileage: dataCar[0].Mileage,
            Vehicle_history: dataCar[0].Vehicle_history
        })

        console.log(this.state);
    }

    componentDidMount = () => {
        Aos.init();
    }

    render() {

        return (
            <Fragment>
                <title>CARSIFY | {dataCar[0].name}</title>
                <div className="container mb-5 mt-5">
                    <div className="row">
                        <img data-aos="fade-right" className="col-lg-5" src={dataCar[0].img} alt={dataCar[0].name} />
                        <div className="col-lg-7">
                            <p className="mt-4">Model : {dataCar[0].Model}</p>

                            <h2>{dataCar[0].name}</h2>
                            <h4>{dataCar[0].price}</h4>

                            <BasicRating />

                            <h5>{this.state.rated}</h5>

                            <Link className="buySingleCar-btn" to="/buy" onClick={this.setStateToNewData} details={this.state.name}>Buy Now</Link>
                        </div>
                    </div>

                    <Buy className='hidden' name={this.state.name}
                        price={this.state.price}
                        img={this.state.img}
                        Reviews={this.state.Reviews}
                        Model={this.state.Model}
                        Exterior_color={this.state.Exterior_color}
                        Interior_color={this.state.Interior_color}
                        Drivetrain={this.state.Drivetrain}
                        MPG={this.state.MPG}
                        Fuel_type={this.state.Fuel_type}
                        Transmission={this.state.Transmission}
                        Engine={this.state.Engine}
                        VIN={this.state.VIN}
                        Stock={this.state.Stock}
                        Mileage={this.state.Mileage}
                        Vehicle_history={this.state.Vehicle_history} />




                    <div className="mt-5 p-2 card col-lg-5">
                        <h3 className="text-center mb-3">Car Details :</h3>
                        <h5>Exterior color : {dataCar[0].Exterior_color}</h5>
                        <h5>Interior color : {dataCar[0].Interior_color}</h5>
                        <h5>Drivetrain : {dataCar[0].Drivetrain}</h5>
                        <h5>MPG : {dataCar[0].MPG}</h5>
                        <h5>Fuel type : {dataCar[0].Fuel_type}</h5>
                        <h5>Transmission : {dataCar[0].Transmission}</h5>
                        <h5>Engine : {dataCar[0].Engine}</h5>
                        <h5>VIN : {dataCar[0].VIN}</h5>
                        <h5>Stock : {dataCar[0].Stock}</h5>
                        <h5>Mileage : {dataCar[0].Transmission}</h5>
                        <h5>Vehicle history : {dataCar[0].Vehicle_history}</h5>
                    </div>

                </div>



            </Fragment>
        )
    }
}

export default CarSignleAudi1






function BasicRating() {
    const [value, setValue] = React.useState(2);

    return (
        <Box
            sx={{
                '& > legend': { mt: 2 },
            }}
        >
            <Typography component="legend">Rate This Car :</Typography>
            <Rating
                name="simple-controlled"
                value={value}
                onChange={(event, newValue) => {
                    setValue(newValue);
                    this.setState({
                        rated : "thank you for your rating"
                    })
                }}
            />
        </Box>
    );
}
